import React, { useState } from 'react';

function TodoList() {
    const [input, updateInput] = useState('');
    const [data, updateData]= useState([]);

    const typing = (e) => {
        updateInput(e.target.value)
    }
    function submit(e){
        e.preventDefault();
        const items = {name:input, id:new Date().getTime().toString()}
        updateData(pre=>{
            return [...pre, items]
        })
        updateInput('')
    }
    return (
        <>
            <div className='todoapp shadow-lg  w-75 w-md-25 p-3'>
                <form onSubmit={submit}>
                    <input onChange={typing} type="text" value={input} />
                    <button type="submit">submit</button>
                </form>
                <div>
                    {data.map(data=>{ return <h6 key={data.id}>{data.name} <span className='ms-5'><button  className='' >delet</button></span><span><button>edit</button></span></h6>})}
                </div>
            </div>
        </>
    );
}

export default TodoList;
