import React, { useState } from 'react';

function TodoList() {

    const [input, updateInput] = useState('')
    const [itemsList, updateItems] = useState([]);

    function typing(event) {
        updateInput(event.target.value)
    }
    function additem(event) {
        event.preventDefault();
        if (input==='') { } else {

            const items = { item: input, id: new Date().getTime().toString() }
            updateItems((pre) => {
                return [...pre, items]

            })
        }
        updateInput('')
    }
    
    function deleteitems(element){
        const updateitems = itemsList.filter((ele)=>{ return element !== ele})
        updateItems(updateitems)
    }
    
    return (
        <>
            <div className='shadow-lg col-md-4 col-10 p-4 todolist '>
                <form className='bg-dark d-flex' onSubmit={additem}>
                    <input type="text" onChange={typing} value={input} placeholder='name' className='w-75 px-2 py-1' />
                    <button type='submit' className='w-25 addelement text-capitalize'>
                        <i className="fas fa-plus"></i>
                    </button>
                </form>
                <div className=' pt-2 d-flex flex-column'>
                    {itemsList.map((items, index) => {
                        return (
                            <>
                                <div className=' d-flex p-1' key={items.id} >
                                    <div className='w-75'> <h6>{items.item}</h6></div>
                                    <div className='w-25 d-flex justify-content-between'>
                                        <button><i className="fas fa-trash " onClick={()=>{deleteitems(items.id)}}></i></button>
                                        <button><i className="fas fa-edit"></i></button>
                                    </div>
                                </div>
                            </>
                        );
                    })}
                </div>
            </div>
        </>
    );
}

export default TodoList;
