import React, { useState} from 'react';

function Todoform() {



    const [input, setInput] = useState('');
    const [itemLIst, setItemList] = useState([]);

    const inputvalues = (e) => {
        setInput(e.target.value)
    }

    const submit = (e) => {
        e.preventDefault();
        const CreateItems = { itemsname: input, id: new Date().getTime().toString() }
        setItemList([...itemLIst, CreateItems])
    }
    return (
        <div>
            <form onSubmit={submit}>
                <input onChange={inputvalues} type="text" name="input" value={input} />
                <button type='submit'>submit</button>
            </form>
            {itemLIst.map((item,index)=>{
                return <ListItems name={item}/>
            })}
        </div>
    );
}

export default Todoform;
