import React, { useState } from 'react';
import Items from './Items';

function TodoList() {
    const [input, updateInput] = useState('');
    const [data, updateData] = useState([]);

    const typing = (e) => {
        updateInput(e.target.value)
    }
    function submit(e) {
        e.preventDefault();
        const items = { name: input, id: new Date().getTime().toString() }
        
        if (input === "") { } else {
            updateData(pre => {
                return [...pre, items]
            })
        }
        updateInput('')
    }
    function deleteitem(id) {
        console.log(id)
        const newData = data.filter(item => {
            return item.id !== id
        })
        updateData(newData)
    }

    return (
        <>
            <div className='todoapp shadow-lg  w-75 w-md-25 p-3'>
                <form onSubmit={submit}>
                    <input onChange={typing} type="text" value={input} />
                    <button type="submit">submit</button>
                </form>
                <div>
                    {data.map(data => { return <h6 key={data.id}>{data.name} <span className='ms-5'><button onClick={() => { deleteitem(data.id) }} className='bg-danger' >delet</button></span><span><button className='bg-warning'>edit</button></span></h6> })}
                    <Items name='tapas'/>
                </div>
            </div>
        </>
    );
}

export default TodoList;
