import React, { useState } from 'react';
import Items from './Items';

function TodoList() {

    const [itemsList, updateItems] = useState([]);
    const [input, updateInput] = useState('')

    function typing(event) {
        updateInput(event.target.value)
    }
    function additem(event) {
        event.preventDefault();

        const items = { item: input, id: new Date().getTime().toString() }
        updateItems((pre) => {
            return [...pre, items]
        })
    }

    return (
        <>
            <div className='shadow-lg col-md-4 col-10 p-4 todolist '>
                <form className='bg-dark d-flex' onSubmit={additem}>
                    <input type="text" onChange={typing} value={input} placeholder='name' className='w-75 px-2 py-1' />
                    <button type='submit' className='w-25 addelement text-capitalize'>
                        <i className="fas fa-plus"></i>
                    </button>
                </form>
                <div className=' pt-2 d-flex flex-column'>
                    {itemsList.map((items, index) => {
                        return (
                            <>
                                <Items value={items} key={index} />
                            </>
                        );
                    })}
                </div>
            </div>
        </>
    );
}

export default TodoList;
