import React, { useState } from 'react';

function TodoList() {

    const [itemsList, updateItems] = useState([]);
    const [input, updateInput] = useState()

    function typing(event){
        const inputvalue = event.target.value
        updateInput(inputvalue)
    }
    function additem(event){
        event.preventDefault();
        if(input)
        updateItems((pre)=>{
            return [...pre, input]
        })
    }

    return (
        <>
            <div className='shadow-lg col-md-4 col-6 p-4 todolist '>
                <form className='bg-dark d-flex'>
                    <input type="text" onChange={typing}  name="items" value={input} className='w-75 px-2 py-1' />
                    <button type='submit' onClick={additem} className='w-25 addelement text-capitalize'>
                        submit
                    </button>
                </form>
                <div className=' pt-2 d-flex flex-column'>
                    {itemsList.map((items) => {
                        return (
                            <>
                                <div className=' d-flex p-1'>
                                    <div className='w-75'> <h6>{items}</h6></div>
                                    <div className='w-25 d-flex justify-content-between'>
                                        <button><i class="fas fa-trash "></i></button>
                                        <button><i class="fas fa-edit"></i></button>
                                    </div>
                                </div>
                            </>
                        );
                    })}
                </div>
            </div>
        </>
    );
}

export default TodoList;
