import React, { useState } from 'react';

function TodoList() {

    const [itemsList, updateItems] = useState(['apple', 'amazon', 'flipkart', 'ekart']);
    return (
        <>
            <div className='shadow-lg col-md-4 col-6 p-4 todolist '>
                <div className='bg-dark d-flex'>
                    <input type="text" name="items" className='w-75 px-2 py-1' />
                    <button type='submit' className='w-25 text-capitalize'>
                        submit
                    </button>
                </div>
                <div className=' pt-2 d-flex flex-column'>
                    {itemsList.map((items) => {
                        return (
                            <>
                                <div className='bg-secondary d-flex p-1'>
                                    <div className='w-75'> <h6>{items}</h6></div>
                                    <div className='w-25 d-flex justify-content-between'>
                                        <button><i class="fas fa-trash "></i></button>
                                        <button> edit</button>
                                    </div>
                                </div>
                            </>
                        );
                    })}
                </div>
            </div>
        </>
    );
}

export default TodoList;
