import React, { useState } from 'react';
import Items from './Items';

function Todoapp() {

    const [input, setInput] = useState('');
    const [Todoitems, setItems] = useState([]);
    const [editingitem, setedititems]= useState();
    const [editTogle, updatetogle] = useState(false);

    // update input item
    const updateinput = (e) => {
        setInput(e.target.value)
    }

    // update object items
    const updateItems = (e) => {
        e.preventDefault();

        if (!input) {

        } else if (editTogle) {
            setItems(Todoitems.map((elem)=>{  if(editingitem.id === elem.id){ return{...elem, } }}))
        }
        else {
            const value = { name: input, id: new Date().getTime().toString() }
            setItems([value, ...Todoitems])
            setInput('')
        }
    }

    // remove object item
    const removeItem = (id) => {
        const newTodoItems = Todoitems.filter((ele) => { return id !== ele.id })
        setItems(newTodoItems)

    }

    // edit object item
    const editItems = (id) => {
        const edititem = Todoitems.find((elem) => { return id === elem.id })
        console.log(edititem)
        updatetogle(true);
        setedititems(edititem);


    }

    return (
        <>
            <div className='todo-app'>
                <form onSubmit={updateItems} >
                    <input onChange={updateinput} type="text" value={input} />
                    <button type='submit'>submit</button>
                </form>
                <div className='items-list mt-3'>
                    {Todoitems.map((items) => { return <Items edit={editItems} remove={removeItem} key={items.id} id={items.id} itemsname={items.name} /> })}
                </div>
            </div>
        </>
    );
}

export default Todoapp;
